import glob, os, time, datetimedef getCurrentStr(line):	startStr="#"	if line.startswith("#") == False:		return line	timeStamp = line[1:11]	timeStamp=timeStamp.replace('\n', '').replace('\r', '').replace(' ', '')	if timeStamp.isdigit() == False or len(timeStamp) < 9:		return line	iTime = int(timeStamp)	if iTime <= 0:		return line	timeArray = time.localtime(iTime)	otherStyleTime = time.strftime("%Y-%m-%d %H:%M:%S", timeArray)	return startStr+otherStyleTime+"\t\t"	destion = "C:\\Users\\Administrator\\desktop\\bash_history"orition = "C:\\Users\\Administrator\\.bash_history"allLines = []with open(orition, 'r+', encoding='utf-8') as f:	lines = f.readlines()	f.seek(0)	for line in lines:		newline = getCurrentStr(line)		allLines.append(newline)with open(destion, 'w', encoding='utf-8') as f:	f.seek(0)	f.writelines(allLines)		